import React, { Component } from 'react';

import {getPlanetByID} from '../../services/sw-service';
import './planet-details.css';

export default class PlanetDetails extends Component {
  constructor(props) {
    super(props)

    this.state = {
      index: 0,
      planet: {}
    }
  }

  handleClick() {
    const nextIndex = this.state.index < 15 ? this.state.index + 1 : 1
    getPlanetByID(nextIndex).then(resp => {
      this.setState({
        planet: resp,
        index: nextIndex
      })
    })
  }

  componentDidMount() {
    this.handleClick()
  }

  onClick = () => {
    this.handleClick()    
  }


  render() {
    return (
      <div className="planet-details card">
          <img alt="planet" className="planet-image" src={this.state.planet.img} /> 
          <div className="card-body">
              <h3>{this.state.planet.name}</h3>
              <ul className="list-group list-group-flush">
                  <li className="list-group-item">
                      <span className="term">Gender: {this.state.planet.diameter}</span> 
                  </li>
                  <li className="list-group-item">
                      <span className="term">Birth Year: {this.state.planet.gravity}</span> 
                  </li>
                  <li className="list-group-item">
                      <span className="term">Eye Color: {this.state.planet.population}</span> 
                  </li>
              </ul>
          </div>
          <button onClick={this.onClick}>Next</button>
      </div>
    )
  }
}
