import React from 'react';
const { default: axios } = require("axios")

export default class Person extends React.Component {

    state = {
        index: 1,
        person: {
            gender: 'init'
        }
    }

    onClick = () => {
        let nextIndex = this.state.index + 1 
        if (nextIndex >= 17) {
            nextIndex = 1
        }
        getPeople(nextIndex).then(resp => {
            console.log(resp)
            this.setState({
                person: resp,
                index: nextIndex
            })
        })

    }

render() {
    return (
        <div className="person-details card">
        <img alt="person" className="person-image" src={this.state.person.img} />

        <div className="card-body">
            <h3>R2-D2</h3>
            <ul className="list-group list-group-flush">
            <li className="list-group-item">
                <span className="term">Gender: </span>
                <span>{this.state.person.gender}</span>
            </li>
            <li className="list-group-item">
                <span className="term">Birth Year: </span>
                <span>{this.state.person.birth_year}</span>
            </li>
            <li className="list-group-item">
                <span className="term">Eye Color: </span>
                <span>{this.state.person.eye_color}</span>
            </li>
            </ul>
        </div>
        <button onClick={this.onClick}>Next</button>
        </div>
    )
    }
}

async function getPeople(nextIndex) {
    const resp = await axios.get(`https://swapi.dev/api/people/${nextIndex}`)
    const img = await axios.get(`https://starwars-visualguide.com/assets/img/characters/${nextIndex}.jpg`)
    console.log(img.data)

    return {
        img: img.data,
        gender: resp.data.gender,
        birth_year: resp.data.birth_year,
        eye_color: resp.data.eye_color
    }
}


