import React, { Component } from 'react';

import {getStarshipByID} from '../../services/sw-service';
import './starship-details.css';

export default class StarshipDetails extends Component {
  constructor(props) {
    super(props)

    this.state = {
      index: 0,
      starship: {}
    }
  }

  handleClick() {
    const nextIndex = this.state.index < 15 ? this.state.index + 1 : 1
    getStarshipByID(nextIndex).then(resp => {
      this.setState({
        starship: resp,
        index: nextIndex
      })
    }).catch(err => {
      this.setState({
        index: nextIndex
      })
    })
  }

  componentDidMount() {
    this.handleClick()
  }

  onClick = () => {
    this.handleClick()    
  }

  render() {
    return (
      <div className="starship-details card">
          <img alt="starship" className="starship-image" src={this.state.starship.img} /> 
          <div className="card-body">
              <h3>{this.state.starship.name}</h3>
              <ul className="list-group list-group-flush">
                  <li className="list-group-item">
                      <span className="term">Gender: {this.state.starship.model}</span> 
                  </li>
                  <li className="list-group-item">
                      <span className="term">Birth Year: {this.state.starship.length}</span> 
                  </li>
                  <li className="list-group-item">
                      <span className="term">Eye Color: {this.state.starship.passengers}</span> 
                  </li>
              </ul>
          </div>
          <button onClick={this.onClick}>Next</button>
      </div>
    )
  }
}
