import React, { Component } from 'react';

import {getPlanetByID} from '../../services/sw-service';
import './planet-details.css';

export default class PlanetDetails extends Component {
  constructor(props) {
    super(props)

    this.state = {
      index: 0,
      planet: {}
    }
  }

  handleClick() {
    const nextIndex = this.state.index < 15 ? this.state.index + 1 : 1
    getPlanetByID(nextIndex).then(resp => {
      this.setState({
        planet: resp,
        index: nextIndex
      })
    })
  }

  componentDidMount() {
    this.handleClick()
  }

  onClick = () => {
    this.handleClick()    
  }


  render() {
    return (
      <div className="card">
        <div className="item-details">
            <img alt="planet-img" className="item-image" src={this.state.planet.img}/>

            <div className="card-body">
            <h3>{this.state.planet.name}</h3>
            <ul className="list-group">
                <li className="list-group-item">
                    <span>Diameter: </span>
                    <span>{this.state.planet.diameter}</span>
                </li>
                <li className="list-group-item">
                    <span>Gravity: </span>
                    <span>{this.state.planet.gravity}</span>
                </li>
                <li className="list-group-item">
                    <span>Population: </span>
                    <span>{this.state.planet.population}</span>
                </li>
            </ul>
            </div>
        </div>
        <button onClick={this.onClick}>Next</button>
      </div>
    )
  }
}
