import React, { Component } from 'react';

import getPersonByID from '../../services/sw-service';
import './person-details.css';

export default class PersonDetails extends Component {
      constructor(props) {
        super(props)

        this.state = {
            index: 0,
            person: {}
        }
    }

    handleClick() {
      const nextIndex = this.state.index < 15 ? this.state.index + 1 : 1
      getPersonByID(nextIndex).then(resp => {
          this.setState({
              person: resp,
              index: nextIndex
          })
      })
    }

    componentDidMount() {
        this.handleClick()
    }

    onClick = () => {
        this.handleClick()    
    }

    render() {
        return (
            <div className="person-details card">
                <img alt="person" className="person-image" src={this.state.person.img} /> 
                <div className="card-body">
                    <h3>{this.state.person.name}</h3>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Gender: {this.state.person.gender}</span> 
                        </li>
                        <li className="list-group-item">
                            <span className="term">Birth Year: {this.state.person.birth_year}</span> 
                        </li>
                        <li className="list-group-item">
                            <span className="term">Eye Color: {this.state.person.eye_color}</span> 
                        </li>
                    </ul>
                </div>
                <button onClick={this.onClick}>Next</button>
            </div>
        )
    }
}
